import React from 'react';
import './style.css'; 
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import BG from '../../image/dh-su-pham-ky-thuat.jpg';

LoginFeature.propTypes = {
    
};

function LoginFeature() {
    return (
        <div className='login-page'>
            <div className='login-bg'>
                <img className='bg' src={BG}/>
            </div>

            <div className='login-area'>
                <p className='text'>ĐĂNG NHẬP</p>
                <form action="#" method="POST" className="u-clearfix u-form-spacing-24 u-form-vertical u-inner-form" style={{padding: '0px'}} source="custom" name="form">
                    <div className="u-form-group u-form-name">
                    <label htmlFor="email" className="u-form-control-hidden u-label" wfd-invisible="true">Email</label>
                    <input type="email" placeholder="Nhập email của bạn" id="email" name="email" className="u-border-2 u-border-black u-border-no-left u-border-no-right u-border-no-top u-input u-input-rectangle u-input-1" onChange={e => setUserName(e.target.value)} required />
                    </div>
                    <div className="u-form-email u-form-group">
                    <label htmlFor="password" className="u-form-control-hidden u-label" wfd-invisible="true">Password</label>
                    <input type="password" placeholder="Nhập mật khẩu của bạn" id="password" name="password" className="u-border-2 u-border-black u-border-no-left u-border-no-right u-border-no-top u-input u-input-rectangle u-input-2" onChange={e => setPassword(e.target.value)} required />
                    </div>
                    <div className="u-align-left u-form-group u-form-submit">
                    <button onClick={handleSubmit} className="u-active-white u-btn u-btn-submit u-button-style u-hover-palette-3-light-2 u-palette-3-base u-btn-1">ĐĂNG NHẬP<br />
                    </button>
                    <input type="submit" defaultValue="submit" className="u-form-control-hidden" wfd-invisible="true" />
                    </div>
                    <input type="hidden" defaultValue name="recaptchaResponse" wfd-invisible="true" />
                </form>
                <Link to='/signup' className="u-border-1 u-border-active-palette-2-base u-border-hover-palette-1-base u-btn u-button-style u-none u-text-palette-1-base u-btn-2">Chưa có tài khoản? Đăng ký ngay</Link>
            </div>
           
        </div>

    );
}
