import React from 'react';
import './style.css'; 
import { Link } from 'react-router-dom';
import BG from '../../image/dh-su-pham-ky-thuat.jpg';
import { useHistory } from 'react-router-dom';

LoginFeature.propTypes = {
    
};

function LoginFeature() {
    const history = useHistory();
    const handleSubmit = () => {history.push('/signup')};

    return (
        <div className='login-page'>
            <div className='login-bg'>
                <img className='bg' src={BG}/>
            </div>

            <div className='login-area'>
                <p className='text'>ĐĂNG NHẬP</p>
                <form method="POST" className="login-form" style={{padding: '0px'}}>
                    <div className="email">
                        <label htmlFor="email" className="email-label" wfd-invisible="true">Email</label>
                        <input type="email" placeholder="Nhập email của bạn" className="email" required />
                    </div>
                    <div className="password">
                        <label htmlFor="password" className="pass-label" wfd-invisible="true">Password</label>
                        <input type="password" placeholder="Nhập mật khẩu của bạn" id="password" name="password" className="password" required />
                    </div>
                    <div className="u-align-left u-form-group u-form-submit">
                        <button onClick={handleSubmit} className="u-active-white u-btn u-btn-submit u-button-style u-hover-palette-3-light-2 u-palette-3-base u-btn-1">ĐĂNG NHẬP<br />
                        </button>
                    </div>
                    <input type="hidden" defaultValue name="recaptchaResponse" wfd-invisible="true" />
                </form>
                <Link to='/signup' className="signup-link">Chưa có tài khoản? Đăng ký ngay</Link>
            </div>
        </div>

    );
}

export default LoginFeature;
