import { render } from '@testing-library/react';
import React, { Component } from 'react';
import './style.css';

class NotiFeature extends Component {
    constructor(props) {
       super(props)
       this.state = {
          students: [
             { id: 1, name: 'Tạ Thị Mai Huong', mssv: 18110298 },
             { id: 2, name: 'Võ Trần Minh Quân', mssv: 18110344 }
          ]
       };

       this.state = {
         isChecked: true,
       };
    }

    toggleChange = () => {
      this.setState({
        isChecked: !this.state.isChecked,
      });
    }

    renderTableData() {
        return this.state.students.map((student, index) => {
          const { id, name, mssv } = student
          return (
            <tr key={id}>
               <td><input type="checkbox"
                     checked={this.state.isChecked}
                     onChange={this.toggleChange}
                  /></td>
              <td>{id}</td>
              <td>{name}</td>
              <td>{mssv}</td>
            </tr>
          )
        })
      }

    render() {
        return (
            <div className='pending-form'>
               <div className='frame-decor'></div>
               <div className='button-group'>
                  <button className='event-btn'>Phê duyệt tất cả</button>
                  <button className='event-btn'>Phê duyệt</button>
                  <button className='event-btn'>Xóa yêu cầu</button>
               </div>
               <table id='students'>
                  <tbody>
                     {this.renderTableData()}
                  </tbody>
               </table>

            </div>
         );
   }
}

export default NotiFeature;
